let poData=$node["Read PDF"].json["text"];

let startIndexPoDetails=poData.indexOf("Purchase Order");
// let endIndexPoDetails=poData.indexOf("Order Value Summary"); 
let endIndexPoDetails=poData.indexOf("In words, RUPEES");

let startIndexPoItems=poData.indexOf("ANNEXURE-I");
let endIndexPoItems=poData.indexOf("Total No. Of Items");

let poItems=poData.substring(startIndexPoItems,endIndexPoItems).split('\n');

let poExtracted=[]; 

let skip=false;
for (i = 0; i < poItems.length; i++) {
  if(poItems[i].includes("Disclaimer")){
    skip=true;
  }
  if(skip && poItems[i].includes("Sr. No.Material Code/DescriptionUOM") && poItems[i+1].includes("Quantity")){
    skip=false;
    i=i+2;
  }
  if(!skip){
    poExtracted.push(poItems[i]);
  }
}

items[0]={
  "poDetails":poData.substring(startIndexPoDetails,endIndexPoDetails).split('\n'),
  "poItems":poExtracted
};

return items[0];

let address=[
    {
        "gstin":"24AAECB3981A1Z3",
