];

let area = {
    pos: {
        id: '',
        po_value: '',
        po_net_value: '',
        po_taxes: '',
        po_other_taxes: '',
        po_date: '',
        po_delivery_date: '',
        shipment_address: '',
        street_address: '',
        gstin: '',
        city: '',
        state: '',
        country: '',
        pincode: '',
        ship_street_address: '',
        ship_pincode: '',
        ship_city: '',
        ship_state: '',
        ship_country: '',
        poc_name: null,
        poc_contact_number: null,
        poc_email: null,
        po_items: [],
        products: [],
        calculated_gross_price:0,
        po_document:'',
        po_value_mismatch:false
    },
}

const poDetaillines = $node["Extract PO Details & Items"].json["poDetails"];

let buyerDetailsFound=false;
let orderValueSummaryFound=false;
let poItemsFound=false;
let completeAddressFound=false;

let shippingAddress=new String();

for (i = 0; i < poDetaillines.length; i++) {

    // PO Number
    if (poDetaillines[i].includes("PO No") && !area.pos.id) {
        area.pos.id = poDetaillines[i].split(":")[1].trim();
    }

    // PO Date
    if (poDetaillines[i].includes("PO Release Date") && !area.pos.po_date) {
        area.pos.po_date = new Date(poDetaillines[i].split(":")[1].trim().split(".").reverse().join("-"));
    }

    //check buyer detials started
    if(poDetaillines[i].includes("Buyer's details")){
        buyerDetailsFound=true;
        i=i+1;
    }    

    if(buyerDetailsFound){
        //Address
        while(!completeAddressFound && !poDetaillines[i].includes("PAN")){
            shippingAddress=shippingAddress+poDetaillines[i]+" ";
            i=i+1;
        }
        completeAddressFound=true;
        if(!area.pos.shipment_address){
            area.pos.shipment_address=shippingAddress.trim();
        }

        // PAN number
        if (poDetaillines[i].includes("PAN") && !area.pan) {            
            area.pan = poDetaillines[i].replace("PAN","");
        }

        // GSTIN
        if (poDetaillines[i].includes("GSTIN") && !area.pos.gstin) {            
            area.pos.gstin = poDetaillines[i].replace("GSTIN","");
        }

        // POC Name
        if (poDetaillines[i].includes("Name") && !area.pos.poc_name) {
            area.pos.poc_name = poDetaillines[i].replace("Name","");
        }

        // POC Contact Number
        if (poDetaillines[i].includes("Phone") && !area.pos.poc_contact_number) {
            area.pos.poc_contact_number = parseInt(poDetaillines[i].replace("Phone","").replace("+",""));
        }

        // POC Email
        if (poDetaillines[i].includes("Email") && !area.pos.poc_email) {
            area.pos.poc_email = poDetaillines[i].replace("Email","");
        }
    }

    //check order value summary started
    if(poDetaillines[i].includes("Order Value Summary")){
        orderValueSummaryFound=true;
    }

    if(orderValueSummaryFound){

        //PO Net Value with out tax
        if (poDetaillines[i].includes("Basic Price") && !area.pos.po_net_value) {
            area.pos.po_net_value = parseFloat(poDetaillines[i+1].replace("INR","").replaceAll(',', '').trim());
        }

        //PO tax
        if (poDetaillines[i].includes("Taxes") && !area.pos.po_taxes) {
            area.pos.po_taxes = parseFloat(poDetaillines[i+1].replace("INR","").replaceAll(',', '').trim());
        }

        //PO other tax
        if (poDetaillines[i].includes("Other Charges") && !area.pos.po_other_taxes) {
            area.pos.po_other_taxes = parseFloat(poDetaillines[i+1].replace("INR","").replaceAll(',', '').trim());
        }

        //PO Value with  tax
        if (poDetaillines[i].includes("PO Price") && !area.pos.po_value) {
            area.pos.po_value = parseFloat(poDetaillines[i+1].replace("INR","").replaceAll(',', '').trim());
        }
    }  
}

//Address Details
let addressDetails=address.find(a => a.gstin == area.pos.gstin);
if(addressDetails){
    area.pos.shipment_address=addressDetails.shipment_address,
    area.pos.street_address=addressDetails.street_address,
    area.pos.city=addressDetails.city,
    area.pos.state=addressDetails.state,
    area.pos.country=addressDetails.country,
    area.pos.pincode=addressDetails.pincode,
    area.pos.ship_street_address=addressDetails.ship_street_address,    
    area.pos.ship_city=addressDetails.ship_city,
    area.pos.ship_state=addressDetails.ship_state,
    area.pos.ship_country=addressDetails.ship_country,
    area.pos.ship_pincode=addressDetails.ship_pincode
}

// All Items
const poItemlines = $node["Extract PO Details & Items"].json["poItems"];

for(j = 0; j < poItemlines.length; j++)
{    

    if(poItemlines[j].includes("ANNEXURE-I") && poItemlines[j+1].includes("PRICE SCHEDULE")){
        poItemsFound=true;
    }

    if(poItemsFound){
        if(poItemlines[j].includes("Sr. No.Material Code/DescriptionUOM") && poItemlines[j+1].includes("Quantity")){
            j=j+2; //Added i+2 to skip 2 lines so that can get serial number and matcode

            //All PRoduct Items Iteration
            while(j<=poItemlines.length && !poItemlines[j]==""){

                // PRODUCTS
                let productDetails = {
                    id: '',
                    product_name: '',
                    hsn: '',
                    product_type: 2
                }


                // PO_ITEMS
                let poItemDetails = {
                    id: '',
                    hsn: '',
                    uom: '',
                    product: '',
                    ordered_qty: '',
                    unit_price: '',
                    net_price: '',
                    gross_price: '',
                    tax_percentage: '',
                    tax_value: '',
                    description: '',
                    delivery_date:''
                }

                let productName=new String();
                let productDescription=new String();

                let serialNumber = poItemlines[j].trim().substring(0,poItemlines[j].trim().length - 10).trim();
                let matCode =poItemlines[j].slice(-10);
                let unitPrice=0;
                let netTotal=0;
                let grossTotal=0;
                let gst=0;
                let totalTax=0;
                let deliveryDate='';

                j=j+1; //Added i+1 to skip 1 line and get HSN
                let hsn=poItemlines[j].replace("HSN No:","").trim();

                //To get Product Name
                j=j+1;
                const decimalRegex = /^-?\d+(\.\d+)?$/;

                while(j<=poItemlines.length &&  (!decimalRegex.test(poItemlines[j].trim()) || !poItemlines[j].includes(".000")) ){
                    productName=productName+poItemlines[j]+" ";
                    j++;                    
                }

                //To get index of occurence of UOM so that in product name it can be removed
                let uomLastIndex=productName.lastIndexOf(poItemlines[j-1]);    
                productName=productName.substring(0,uomLastIndex).trim();  

                // To get UOM
                let uom=poItemlines[j-1].trim();

                //To get quantity
                let qty=parseFloat(poItemlines[j].replaceAll(',', '').trim());

                //To get Product Description
                j=j+1;
                while(j<=poItemlines.length && !poItemlines[j].includes("Currency")){
                    productDescription=productDescription+poItemlines[j]+" ";
                    j++;
                }                 

                //To loop till product detials ends
                while(j < poItemlines.length-1 && !poItemlines[j].replace(/ /g, "").includes("DateQuantityPlant")){
                    if(poItemlines[j].includes("Item Basic PriceINR")){
                        let unitAndNetTotal=poItemlines[j+1].trim().split(" ");
                        unitPrice=parseFloat(unitAndNetTotal[0].replaceAll(',', '').trim());
                        netTotal=parseFloat(unitAndNetTotal[1].replaceAll(',', '').trim());
                    }
                    if(poItemlines[j].includes("Item PO Price")){
                        grossTotal=parseFloat(poItemlines[j+1].replaceAll(',', '').trim());
                    }
                    if(poItemlines[j].includes("CGST")){
                        gst=parseFloat(poItemlines[j].replace("CGST","").replaceAll('%','').trim())*2;
                        totalTax=parseFloat(poItemlines[j+1].replaceAll(',', '').trim())*2;
                    }
                    if(poItemlines[j].includes("IGST")){
                        gst=parseFloat(poItemlines[j].replace("IGST","").replaceAll('%','').trim());
                        totalTax=parseFloat(poItemlines[j+1].replaceAll(',', '').trim());
                    }
                    j++;
                }   

                deliveryDate=poItemlines[j+1]?new Date(poItemlines[j+1].split(" ")[0].split(".").reverse().join("-")):"";  
                if(!area.pos.po_delivery_date || area.pos.po_delivery_date < deliveryDate){
                    area.pos.po_delivery_date= deliveryDate;
                }
                j=j+2;

                poItemDetails.id=area.pos.id+"-"+serialNumber;
                poItemDetails.hsn=hsn;
                poItemDetails.product=matCode;
                poItemDetails.uom=uom;
                poItemDetails.ordered_qty=qty;
                poItemDetails.description=productDescription;
                poItemDetails.unit_price=unitPrice;
                poItemDetails.net_price=netTotal;
                poItemDetails.gross_price=grossTotal;
                poItemDetails.tax_percentage=gst;
                poItemDetails.tax_value=totalTax;
                poItemDetails.delivery_date=deliveryDate; 


                productDetails.id=matCode;
                productDetails.hsn=hsn;
                productDetails.product_name=productName?productName:productDescription.substring(0,50);

                area.pos.po_items.push(poItemDetails);
                area.pos.products.push(productDetails);
                area.pos.calculated_gross_price=area.pos.calculated_gross_price+(unitPrice*qty)+((unitPrice*qty)*(gst/100));               

            }
        }        
    }
}

let products = area.pos.products;
area.pos.po_document = $node["Post the file"].json["data"]["id"];
area.pos.po_value_mismatch=area.pos.calculated_gross_price==area.pos.po_value?false:true;

let productIds = products.map(product => product.id);
items[0].json.area = area;
items[0].json.productIds = productIds;
items[0].json.products = products;

return items;
