let orderNumber =  items[0].json["order_id"]
let po_num =  items[0].json["po_number"]
orderStatus= items[0].json["order_status"]
items[0].json.orderStatus = orderStatus
if(orderStatus ==="processed"){
   items[0].json.subject = `Order ${orderNumber} for Purchase Order ${po_num} has been Processed`
   items[0].json.heading = `Order ${orderNumber} against PO ${po_num} has processed`
  items[0].json.bodyContent = `Order ${orderNumber} against PO ${po_num} 
has been processed and will shortly shipped to it’s destination. 
We will keep you updated with the status of your shipment.`
}
if(orderStatus==="intransit"){
   items[0].json.subject = `Order ${orderNumber} for Purchase Order ${po_num} 
is In Transit`
   items[0].json.heading = `Order ${orderNumber} against PO ${po_num} has 
been Dispatched`
   items[0].json.bodyContent = `Order  ${orderNumber} against PO ${po_num} 
has been Shipped and will arrive at it’s destination soon.`
}

if(orderStatus === "complete"){
    items[0].json.subject = `Order ${orderNumber}  for Purchase Order ${po_num} 
has reached it’s destination`
    items[0].json.heading = `: Order ${orderNumber} against PO ${po_num} has 
reached it’s Destination.`
     items[0].json.bodyContent = `: Order ${orderNumber} against PO ${po_num} 
has been reached it’s destination and is now complete.`

}


let order_prefix = $node["Globals"].json["order_prefix"]
let add_prefix = order_prefix+orderNumber 

items[0].json.add_prefix = add_prefix 
return items;
