parameters.functionCode:
let orderNumber =  $node["Get po_number"].json.data["order_id"]

let po_number =  $node["Get po_number"].json.data["vendor_invoice_number"]["vendor_po_num"]["po_num"]["po_num"];
let orderStatus= $node["Globals"].json["order_status"];

items[0].json.orderStatus = orderStatus
if(orderStatus ==="processed"){
   items[0].json.subject = `Order ${orderNumber} for Purchase Order ${po_number} has been Processed`
   items[0].json.heading = `Order ${orderNumber} against PO ${po_number} has processed`
  items[0].json.bodyContent = `Order ${orderNumber} against PO ${po_number} 
has been processed and will shortly shipped to it’s destination. 
We will keep you updated with the status of your shipment.`
}
if(orderStatus==="intransit"){
   items[0].json.subject = `Order ${orderNumber} for Purchase Order ${po_number} 
is In Transit`
   items[0].json.heading = `Order ${orderNumber} against PO ${po_number} has 
been Dispatched`
   items[0].json.bodyContent = `Order  ${orderNumber} against PO ${po_number} 
has been Shipped and will arrive at it’s destination soon.`
}

if(orderStatus === "complete"){
    items[0].json.subject = `Order ${orderNumber}  for Purchase Order ${po_number} 
has reached it’s destination`
    items[0].json.heading = `: Order ${orderNumber} against PO ${po_number} has 
reached it’s Destination.`
     items[0].json.bodyContent = `: Order ${orderNumber} against PO ${po_number} 
has been reached it’s destination and is now complete.`

}

let order_prefix = $node["Globals"].json["order_prefix"]

let remove_prefix = orderNumber.replace(order_prefix,"");


items[0].json.remove_prefix =remove_prefix 

items[0].json.po_number = po_number 
items[0].json.orderNumber = orderNumber;
items[0].json.orderStatus = orderStatus
return items;

let products = $node["Get the products and orders"].json.data[0].products;
let orders = $node["Get the products and orders"].json.data[0].orders;
let req_qty = 0;
let placed_qty = 0;
let po_status = "";
products.forEach(function(product,index){
  req_qty=req_qty+parseInt(product["req_qty"])
})
orders.forEach(function(order,index){
  order.order_details.forEach(function(obj,idx){
    placed_qty = placed_qty+parseInt(obj.qty_placed)
  })
})

if(req_qty == placed_qty ){
  po_status= "payment_pending"
}
items[0].json.products = products;
items[0].json.orders = orders;
items[0].json.req_qty = req_qty;
items[0].json.placed_qty = placed_qty;
items[0].json.po_status = po_status;
return items;
