parameters.functionCode:
let allLines = $node["Read PDF"].json["text"].split("\n")
let payment_advices = $node["Get the PO Number"].json["data"][0]["sd_mozart_shakedeal_payments"]
items[0].json.text = allLines 


for(i=0;i<allLines.length;i++){
    if(allLines[i].includes("INV")){
       let trimLine = allLines[i].trim()
       let splittedLine =trimLine.split(" ");
       let trimAmountLine = allLines[i+1].trim();
       let splitAmountLine = trimAmountLine.split(" ");
       items[0].json.invoice_amount = parseFloat(splitAmountLine[0])
       items[0].json.invoice_number = splittedLine[0]


    }else if(allLines[i].includes("Bank Ref")){
           let utrLine= allLines[i].split(":");


           items[0].json.utr = utrLine[1]


    }



}
let filtered_payment_obj = payment_advices.filter(obj=>obj["payment_advice_file"]==$node["Get Payment Advice"].json["body"]["payload"]["payment_advice_file"])
let payment_advice = []
payment_advice[0] = {}
payment_advice[0].payment_utr =  items[0].json.utr
payment_advice[0].invoice_number = items[0].json.invoice_number
payment_advice[0].payment_paid=  items[0].json.invoice_amount
items[0].json.filtered_payment_obj = filtered_payment_obj 
items[0].json.payment_advice =payment_advice 
return items;
