parameters.functionCode:
let data = $node["Get the POs "].json["data"]
let a='';
for(i=0;i<data.length;i++){
  let diffDays = "";
  let style = "";
  let delayed_reason = "";
     var dateFirst = new Date();
     var dateSecond = new Date(data[i]["po_deliver_date"]);
     var timeDiff = Math.abs(dateSecond.getTime() - dateFirst.getTime());
     diffDays = Math.ceil(timeDiff / (1000 * 3600 * 24));
     if(diffDays<=5){
         style = "background-color:#F7971C"
     }else{
         style = "background-color:#F2C94C"
     }
     if(diffDays != 1){
        if(data[i]["delivery_status"]!="1_delayed"){
          diffDays = diffDays+" "+"days remaining"
        }
         else{
           diffDays = "Delayed by"+" "+diffDays+" "+"days"
           style = "background-color:#E35169"
         }
     }else{
        if(data[i]["delivery_status"]!="1_delayed"){
          diffDays = diffDays+" "+"day remaining"
        }
         else{
           diffDays = "Delayed by"+" "+diffDays+" "+"day"
           style = "background-color:#E35169"
         }
     }
     if(data[i]["delayed_reason"]){
       delayed_reason = data[i]["delayed_reason"]
     }else{
       delayed_reason = "--"
     }
  a=a+`<tr style=${style}><td>${i+1}</td><td>${data[i]["po_num"]}</td><td>${data[i]["po_deliver_date"]}</td><td>${diffDays}</td><td>${delayed_reason }</td></tr>`;
}
items[0].json.html = `<table  style="color:#fff;width: auto;"><tr style="background-color:#a7d"><th>SL.No</th><th>PO Number</th><th>Delivery Date</th><th>Due in Days</th><th>Delayed Reason</th></tr>${a}</table>`;
items[0].json.po= data 

return items;
