let responseData = [];

const affordableCataglogs = [];
const catalogMode = $node["Get the link details"].json["data"][0]["reward_campaign"]["catalog_mode"];

$node["Get the link details"].json["data"][0]["reward_campaign"]["catalogue"].forEach(cat=>{
    const { sp_reward_sku_id } = cat;
    const { value } = sp_reward_sku_id;
    if(catalogMode == "eq"){
        if(value*1==$node["Get the link details"].json["data"][0]["pending_value"]*1) {
            affordableCataglogs.push(cat);
        } 
    }else{
        if(value*1<=$node["Get the link details"].json["data"][0]["pending_value"]*1) {
            affordableCataglogs.push(cat);
        }
    }

})

responseData.push({
    json: {
        "success":true,
        "message":"Reward Link fetched!!",
        "data":{
            "end_date": $node["Get the link details"].json["data"][0]["end_date"], 
            "value": $node["Get the link details"].json["data"][0]["value"],
            "pending_value": $node["Get the link details"].json["data"][0]["pending_value"],
            "email": $node["Get the link details"].json["data"][0]["email"],
            "phone": $node["Get the link details"].json["data"][0]["phone"],
            "otp_mode": $node["Get the link details"].json["data"][0]["otp_mode"], 
            "status": $node["Get the link details"].json["data"][0]["status"],
            "sku": $node["Get the link details"].json["data"][0]["sku"],
            "reward_campaign": {
            // "image": `${$node["Globals"].json["dms_url"]}/assets/${$node["Get the link details"].json["data"][0]["reward_campaign"]["brands"][0]["brand_id"]["image"]}`,
            "campaign_type": $node["Get the link details"].json["data"][0]["reward_campaign"]["campaign_type"],
            "tnc": $node["Get the link details"].json["data"][0]["reward_campaign"]["tnc"],
            "bg_image": `${$node["Globals"].json["dms_url"]}/assets/${$node["Get the link details"].json["data"][0]["reward_campaign"]["bg_image"]}`,
            "catalogue":affordableCataglogs,
            "catalogue_mode": catalogMode,
            },
            "redemptions": $node["Get the link details"].json["data"][0]["redemptions"],
        }
    }				
});

return responseData;

let responseData = [];

responseData.push({
    json: {
        "success": false,
        "message": "invalid link"
    }				
});

return responseData;
