let order_details = Array.isArray($node["Get the order_id"].json.data)? $node["Get the order_id"].json.data[0].order_items:$node["Get the order_id"].json.data.order_items;
let product_codes  =  $node["Function to get product codes"].json["product_codes"];
let existing_products = $node["Get the products based on product codes"].json.data;
let resultFilter = [];
let action = "";
var toFindDuplicates = product_codes.reduce(function(acc, el, i, arr) {
  if (arr.indexOf(el) !== i && acc.indexOf(el) < 0) acc.push(el); return acc;
}, []);

toFindDuplicates.forEach(function(code,index){
  let filterProducts = order_details.filter(prod => prod["sdm_po_items_id"]["product"]["id"] == code);
  let filterExistingProducts = existing_products.filter(prod=>prod.product_code == code);
  if(filterProducts.length>filterExistingProducts.length){
    for(i=0;i<=filterProducts.length-filterExistingProducts.length;i++){
        resultFilter.push(filterProducts[i]);
    }
  }
});

let products_to_create = []
items[0].json.length= resultFilter .length;
if(resultFilter.length>0){
   action="post the products";
   resultFilter.forEach(function(obj,index){
     products_to_create[index]={};
     products_to_create[index].product_name = obj["sdm_po_items_id"]["product"]["product_name"]
     products_to_create[index].product_code = obj["sdm_po_items_id"]["product"]["product_code"]
     products_to_create[index].hsn = obj["sdm_po_items_id"]["product"]["hsn"]
     products_to_create[index].description = obj["sdm_po_items_id"]["product"]["description"]
   })

}else action = "no products to post";

items[0].json.resultFilter = resultFilter;
items[0].json.action = action;
items[0].json.products_to_create = products_to_create;
return items;

let order_to_products = [];
let concat_products = $node["Function to create product"].json.action == "post the products"?[...$node["Create the products in vpanel"].json.data,...$node["Get the products based on product codes"].json.data]:$node["Get the products based on product codes"].json.data;
concat_products.map((obj, i) => ({ ...obj, isMapped: false }));
let product_details = Array.isArray($node["Get the order_id"].json.data)? $node["Get the order_id"].json.data[0].order_items:$node["Get the order_id"].json.data.order_items;
product_details.forEach(function(obj,index){
  let product = concat_products.filter(prod=>prod.product_code == obj["sdm_po_items_id"]["product"]["id"] && !prod["isMapped"]);
  concat_products[concat_products.findIndex(prod=>prod.product_code == obj["sdm_po_items_id"]["product"]["id"]&& !prod["isMapped"])].isMapped = true;
  order_to_products[index]={};
  order_to_products[index].sdv_products_id = product[0].id;
  order_to_products[index].qty = obj.quantity
  order_to_products[index].product_total = obj.sdm_po_items_id.gross_price
})

order_obj = {};
//order_obj.invoice_file = $node["Post the file to vpanel"].json.data.id;
order_obj.order_id = $node["Update the order details in order"].json["data"]["order_id"];
order_obj.order_value = $node["Get the order_id"].json.data[0].order_value;
order_obj.order_date = $node["Get the order_id"].json.data[0].date_created;
order_obj.order_status = $node["Get the order_id"].json.data[0].order_status;
order_obj.invoice_number = Array.isArray($node["Get the shipment id"].json.data)?$node["Get the shipment id"].json.data[0].id:$node["Get the shipment id"].json.data.id;
order_obj.products = order_to_products

items[0].json.order_obj = order_obj;
items[0].json.order_to_products= order_to_products;
items[0].json.concat_products= concat_products;
items[0].json.product_details= product_details;
return items;

let advance_payments = $node["Get pr_advance payment"].json.data;
let order_value = parseInt($node["Get the po_details"].json["data"]["shipment"]["invoice_value"]);
let remaining_order_value = order_value;
let update_residual_amount_obj = [];
let payment_status = "";
let max_percentage = $node["Globals"].json["max_percentage"];
let min_percentage = $node["Globals"].json["min_percentage"];
if(advance_payments.length>0){
  advance_payments.forEach(function(payment,index){
    if(remaining_order_value>0){
      let residual_amount = remaining_order_value>=payment.residual_amount?0:(payment.residual_amount-remaining_order_value);
      remaining_order_value= payment.residual_amount>=remaining_order_value?0:remaining_order_value-payment.residual_amount;
      update_residual_amount_obj[index] = {};
      update_residual_amount_obj[index].id = payment.id;
      update_residual_amount_obj[index].residual_amount = residual_amount
    }
  })
}
let total_paid_amount = order_value - remaining_order_value;
let percentage_paid = (total_paid_amount*100)/order_value;
if(percentage_paid>=max_percentage){
  payment_status="paymentreleased"
}else if(percentage_paid>min_percentage && percentage_paid<90){
  payment_status="partiallypaid"
}else payment_status="paymentpending"



items[0].json.total_paid_amount = total_paid_amount;
items[0].json.advance_payments = advance_payments;
items[0].json.order_value = order_value;
items[0].json.remaining_order_value = remaining_order_value;
items[0].json.update_residual_amount_obj = update_residual_amount_obj;
items[0].json.percentage_paid = percentage_paid;
items[0].json.payment_status = payment_status;
return items;

const update_residual_amount_obj = $node["Function to create payment to vendors"].json["update_residual_amount_obj"]

let returnItems =[]

update_residual_amount_obj.forEach(function(obj,index){  
    returnItems.push({
    json: {
       id : obj.id,
       residual_amount : obj.residual_amount,
       url : $node["Globals"].json["dms_url"]
    }
  });

})

return returnItems;
