//function to get the round value with precision
function round(value, precision) {
    var multiplier = Math.pow(10, precision || 0);
    return Math.ceil(value * multiplier) / multiplier;
}

order_details.forEach(function(product,index){
    let matched_product = product_details.filter(obj=>product.sd_mozart_vendor_product_mapping_id.product.product_code == obj.sd_mozart_vendor_product_mapping_id.product.product_code);
    let product_price = matched_product[0].rate*product.qty_placed + (matched_product[0].rate*product.qty_placed*matched_product[0].tax_percentage)/100;
    order_total = order_total+ product_price;
    sub_total = sub_total + matched_product[0].rate*product.qty_placed;
    products[index]={};
    products[index].product = product.sd_mozart_vendor_product_mapping_id.product.product_name;
    products[index].productsku = product.sd_mozart_vendor_product_mapping_id.product.product_code;
    //products[index].hsn_code = product.sd_mozart_vendor_product_mapping_id.product.hsn;
    products[index].hsn_code = "12345678"
    products[index].tax_ids = "";
    //products[index].price = product_price;
    products[index].price = (matched_product[0].rate*matched_product[0].tax_percentage/100)+parseFloat(matched_product[0].rate);
    //products[index].pri = matched_product[0].rate;
    //products[index].rate_value = matched_product[0].tax_percentage.toString();
    products[index].rate_value = "18.000";
    products[index].pricecalculation = "on";
    products[index].amount = product.qty_placed.toString();
    products[index].status="D";
    products[index].main_category = "3622";
    products[index].tax_ids = "13"
    //update the order details obj to update the order details in order
    product.rate = matched_product[0].rate;
    product.product_total = product_price;
    product.tax_value = matched_product[0].tax_percentage;
    product.sub_total = matched_product[0].rate*product.qty_placed;
    product.plant = $node["Get the po_details"].json["data"]["vendor_invoice_number"]["vendor_po_num"]["product_details"][0]["plant"];
    product.sd_mozart_vendor_product_mapping_id.vendor = vendor_id;
}); 
