let action = "";
let products = $node["Get po_details"].json["data"][0]["po_items"];
let product_codes = $node["Function for getting sku"].json.product_codes;
let existing_product_codes = [];

let existing_products = items[0].json.data
let resultFilter = 
   products.filter(products=>
     !existing_products.some(
      data=> data.product_code === products["product"]["id"] 
    )
  );

existing_products.forEach(function(prod,index){
  existing_product_codes.push(prod.product_code);
});

var toFindDuplicates = product_codes.reduce(function(acc, el, i, arr) {
  if (arr.indexOf(el) !== i && acc.indexOf(el) < 0) acc.push(el); return acc;
}, []);

toFindDuplicates.forEach(function(code,index){
  let filterProducts = products.filter(prod => prod["product"]["id"] == code);
  let filterExistingProducts = existing_products.filter(prod=>prod.product_code == code);
  if(filterProducts.length>filterExistingProducts.length){
    resultFilter.push(filterProducts[0]);
  }
});


let products_to_create = []
items[0].json.length= resultFilter .length;
if(resultFilter.length>0){
   action="post the products";
