const pdf = require('html-pdf-node');
const htmlContent = `
  <html>
    <body>
      <h1>Hello, world!</h1>
    </body>
  </html>
`;

// set the PDF options
const pdfOptions = { format: 'Letter' };

// generate the PDF document and store it in a buffer
const pdfBuffer = await new Promise((resolve, reject) => {
  pdf.create(htmlContent, pdfOptions).toBuffer((err, buffer) => {
    if (err) {
      reject(err);
    } else {
      resolve(buffer);
    }
  });
});

const pdfBinary = new Binary(pdfBuffer);

// set the binary data as the output of the function node
return { binary: pdfBinary };

