let dataArray = $node["Webhook"].json["body"];
let modifiedContentArray1 = [];
let content_array = $node["Webhook"].json["body"]["modifiedContentArray"];
let content_field = $node["Webhook"].json["body"]["content_data"];

for (let i = 0; i < content_array.length; i++) {
  let content = content_field; // Copy the original content_data
  let currentItem = content_array[i];
let formattedDate1 = currentItem.enddate;
      const date = new Date(formattedDate1);
  const options = { day: '2-digit', month: 'short', year: 'numeric' };
  const formattedDate = date.toLocaleDateString('en-US', options);
  // Replace placeholders in the content with corresponding values from the current item
  content = content.replace(/{{link}}/g, currentItem.linkId)
    .replace(/{{img}}/g, currentItem.imageSrc)
    .replace(/{{shakepelink}}/g, currentItem.shakepelink)
    .replace(/{{enddate}}/g, formattedDate)
    .replace(/{{name}}/g, currentItem.name);

let linkObj = {
        content:content ,
        shakepelink: currentItem.shakepelink,
        name: currentItem.name,
        imageSrc: currentItem.imageSrc,
        enddate:formattedDate,
        email:currentItem.email,
        subject:currentItem.subject,
        from:currentItem.from,
        linkId: currentItem.linkId

      };


  modifiedContentArray1.push(linkObj);
}

return { modifiedContentArray1 };
