const newItems = [];
for (let item of items[0].json.body) {
  item.dmsUrl = $item(0).$node["Globals"].json["dmsUrl"];
  newItems.push({json: item});
}
return newItems;

let poDetailsTemplate = '<tr class=""><td class="text" data-title="Order:">{{order_id}}</td><td class="text" data-title="PO:">{{po_id}}</td><td class="number" data-title="PO Value:">{{po_value}}</td><td class="text " data-title="Vendor Name:">{{vendor_name}}</td><td class="text" data-title="Vendor Email:">{{vendor_email}}</td></tr>';
let newItem = {json: {}};

let poDetails = '';
for (const item of items) {
  let data = item.json.data;
  poDetails += poDetailsTemplate;
  poDetails = poDetails.replace("{{order_id}}", data.parent_order);
  poDetails = poDetails.replace("{{po_id}}", data.id);
  poDetails = poDetails.replace("{{po_value}}", data.po_value);
  poDetails = poDetails.replace("{{vendor_name}}", data.vendor_name);
  poDetails = poDetails.replace("{{vendor_email}}", data.vendor_email);
  newItem.json.order_id = data.parent_order;
}
newItem.json.po_details = poDetails;
return [newItem];

