const axios = require("axios");

try {
  const n8nUrl = $node["Globals"].json["n8n_url"] + "/webhook/send-otp";
  const dmsUrl = $node["Globals"].json["dms_url"];

  const selectedCardsForSMS = $node["Get Details From Reference"].json["data"];

  const sendMSG91Auth = $node["Globals"].json["msg91_auth"];
  const dmsAuth = $node["Globals"].json["dms_auth"];

  const successKeys = [];
  const failureKeys = [];

  for (let i = 0; i < selectedCardsForSMS.length; i++) {
      const status = $node["Get Details From Reference"].json["data"][i]["status"];
      const mobileNumber = $node["Get Details From Reference"].json["data"][i]["mobile_number"];

      if(status=="AssignedToUser" && mobileNumber && mobileNumber.length) {
          const smsConfig = {
            mobile: "91"+mobileNumber,
            otp: "",
            source: "ShakePe StepUp Activation",
            sender_id: "SHAKPE-Card-Distribution",
            params: [ $node["Get Details From Reference"].json["data"][i]["amount"],  $node["Get Details From Reference"].json["data"][i]["card_number"], $node["Get Details From Reference"].json["data"][i]["reference_number"] ]
          }

          const sendSmsResponse = await axios.post(`${n8nUrl}`, smsConfig, {
            headers: {
              'Authorization': sendMSG91Auth,
              'Content-Type': 'application/json'
            }
          });

          const success = sendSmsResponse.data["success"];

          if(success) {
            successKeys.push($node["Get Details From Reference"].json["data"][i]["sd_serial_number"])
          } else {
            failureKeys.push($node["Get Details From Reference"].json["data"][i]["sd_serial_number"])
          }
      }


  }

  let saveSmsSuccessStatusResponse = null;
  let saveSmsFailureStatusResponse = null;

  if(successKeys.length) {
    saveSmsSuccessStatusResponse = await axios.patch(`${dmsUrl}/items/sp_card_inventory`, {
      "keys": successKeys,
      "data": {
        "user_assigned_sms": true
      }
    }, {
      headers: {
        'Authorization': dmsAuth,
        'Content-Type': 'application/json'
      }
    });
  }

  if(failureKeys.length) {
    saveSmsFailureStatusResponse = await axios.patch(`${dmsUrl}/items/sp_card_inventory`, {
      "keys": failureKeys,
      "data": {
        "user_assigned_sms": false
      }
    }, {
      headers: {
        'Authorization': dmsAuth,
        'Content-Type': 'application/json'
      }
    });
  }


  return { smsDelivery: true, responses: { successKeys, failureKeys } };
} catch(e) {
  return { smsDelivery: false, error: e };
}
