let responseData = [];

const cardDataArr = $node["Get Details From Reference"].json["data"];

if(cardDataArr.length) {
  const { mobile_number, status } = cardDataArr[0];
  let success = false;
  let danger = false;
  let message = "";

  switch(status) {
    case "NotAssigned": {  
      success = false;
      danger = true;
      message = "Card not yet assigned";
      break;
    };
    case "Assigned": {  
      success = true;
      message = "Mobile number retireved!!";      
      break;
    };
    case "RequestForActivation": {    
      success = false;
      danger = true;
      message = "We have already received your request. Your card will be activated shortly.";      
      break;
    };
    case "Activated": {  
      success = false;
      message = "Your card is already activated and ready to use."; 
      break;
    };
    case "Blocked": {  
      success = false;
      danger = true;
      message = "Your card is temporarily blocked. Please contact ShakePe support at: <a href='mailto:support@shakepe.com'><u>support@shakepe.com</u></a>"; 
      break;
    };
    default:  {  
      success = true;
      message = "Mobile number retireved!!"; 
      break;
    };
  }

  responseData.push({
      json: {
        success,
        message,
        mobile_number,
        danger
      }				
  });
} else {
  responseData.push({
      json: {
        "success":false,
        "message":"Error Occurred, please try after sometime.",
        "mobile_number": null,
        danger: true
      }				
  });
}

return responseData;
