let responseData = [];

const cardDataArr = $node["Get Details From Serial"].json["data"];

if(cardDataArr.length) {
  const { mobile_number, status } = cardDataArr[0];
  let success = false;
  let message = "";

  switch(status) {
    case "NotAssigned": {  
      success = false;
      message = "Card not yet assigned";
      break;
    };
    case "Assigned": {  
      success = true;
      message = "Mobile number retireved!!";      
      break;
    };
    case "RequestForActivation": {    
      success = false;
      message = "We have already received your request. Your card will be activated shortly.";      
      break;
    };
    case "Activated": {  
      success = false;
      message = "Your card is already activated and ready to use."; 
      break;
    };
    case "Blocked": {  
      success = false;
      message = "Your card is temporarily blocked. Please contact ShakePe support at: support@shakepe.com"; 
      break;
    };
    default:  {  
      success = true;
      message = "Mobile number retireved!!"; 
      break;
    };
  }

  responseData.push({
      json: {
        success,
        message,
        mobile_number
      }				
  });
} else {
  responseData.push({
      json: {
        "success":false,
        "message":"Invalid Serial Number",
        "mobile_number": null
      }				
  });
}

return responseData;
