for (var key in products) {
    if (products.hasOwnProperty(key)) {
        var product = products[key];

        product_data[count]={};
        product_data[count].product_code = product.product_code;
        product_data[count].product_name = product.product;

        let product_price = product.price;
        let product_qty = product.amount;
        let tax_percentage = product.prod_tax_rate;
