let order_response = $node["Webhook"].json.body;

let products = order_response.products;

let product_data = [];

let count = 0;
let total_gross_value = total_tax_value = total_net_value = 0;

//Product Details
for (var key in products) {
    if (products.hasOwnProperty(key)) {
        var product = products[key];

        product_data[count]={};
        product_data[count].product_code = product.product_code;
        product_data[count].product_name = product.product;

        let product_price = product.price;
        let product_qty = product.amount;
        let tax_percentage = product.prod_tax_rate;
        let net_amount = product_price / ((100+tax_percentage)/100);
        let product_net_amount = net_amount * product_qty;
        let total_amount = product_price * product_qty;
        let product_tax_value = total_amount - product_net_amount;

        product_data[count].price = product_price;

        product_data[count].ordered_qty = product_qty;

        product_data[count].tax_percentage = tax_percentage;

        product_data[count].product_net_value = product_net_amount;
        product_data[count].product_tax_value = product_tax_value;

        product_data[count].product_gross_value = total_amount;


        total_gross_value = total_gross_value + total_amount;
        total_tax_value = total_tax_value + product_tax_value;
        total_net_value = total_net_value + product_net_amount;

        count++;
    }
}

