        let product_tax_value = total_amount - product_amount_without_tax;

        parent_order_total_gross_value = parent_order_total_gross_value + total_amount;
        parent_order_total_tax_value = parent_order_total_tax_value + product_tax_value;
        parent_order_total_net_value = parent_order_total_net_value + product_amount_without_tax;

