let poFiles=[];
let excelFiles=[];
items.forEach((item) => {
    const fullPath = item.json["path"].split("/");
    const fileName = fullPath[fullPath.length - 1]
    if(decodeURI(fileName).includes(".csv") || decodeURI(fileName).includes(".xlsx") ||decodeURI(fileName).includes(".xls")){
        excelFiles.push({"file_name":decodeURI(fileName)});
    }
});

return [{
     json: {
         excelData:excelFiles
    }
}];

let binaries = {};
const { read: xlsxRead } = require('xlsx'); 
const data = items[0].binary.data; 
workbook = xlsxRead(data.data); 
binaries["data"] = workbook;

items[0].json.binaries= binaries
return items;

let issuanceData = [];
let products = [];
items.forEach((item, index) => {
  if(item.json["Movement type"] == "101"){
    const orderDate = getFormattedDate(item.json["Posting Date"]);
    products.push(item.json["Material"])
    issuanceData.push({
      po_number : item.json["Purchase order"],
      product : item.json["Material"],
      plant : item.json["Plant"],
      qty : item.json["Qty in unit of entry"],
      order_date : orderDate
    })
  }
})
function getFormattedDate(secs){
  const millisecondsPerDay = 24 * 60 * 60 * 1000; // Number of milliseconds in a day

  const date = new Date((secs-1) * millisecondsPerDay + Date.parse("1900-01-01"));

  const day = date.getDate();
  const month = date.getMonth() + 1; // Months are zero-based, so we add 1
  const year = date.getFullYear();
  const formattedDate = `${year}-${month < 10 ? '0' : ''}${month}-${day < 10 ? '0' : ''}${day-1}`;
  return formattedDate
}
let uniqueProductArr = products.filter((value, index) => {
    return products.indexOf(value) === index;
  });
return [{
    json: {
        issuanceData: issuanceData,
        products : uniqueProductArr
    },
}];

let issuances = $node["Function to get issuance data"].json["issuanceData"];
let shipmentItems = $node["Get the shipment Items"].json["data"];
let orderObj = [];
let updateShipmentItems = [];
let testConsole = [];
uniqueArr = Object.create(null),
    uniqueProductPoPlantArr = issuances.filter(o => {
        var key = ['po_number','plant'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });
uniqueProductPoPlantArr.forEach(function(product,index){
    let matchedIssuances = issuances.filter(issuance =>product.po_number == issuance.po_number && product.plant == issuance.plant );
    uniqueIssuanceArr = Object.create(null),
    uniqueProductArr = matchedIssuances.filter(o => {
        var key = ['product'].map(k => o[k]).join('|');
        if (!uniqueIssuanceArr[key]) {
            uniqueIssuanceArr[key] = true;
            return true;
        }
    }); 
    //let matchedShipmentItems = shipmentItems.filter(shipmentItem => product.product == shipmentItem.sdm_po_items_id.product && product.plant == shipmentItem.sdm_po_items_id.plant);
    let orderProducts = [];
    let orderItems = [];
    let shipments = [];
    let orderValue = 0;
    uniqueProductArr.forEach(function(unqProduct,index){
        let matchedProducts = matchedIssuances.filter(matchedIssuance =>matchedIssuance.product == unqProduct.product);
        orderProducts[index] = {};
        orderProducts[index].product = unqProduct.product;
        orderProducts[index].qty = matchedProducts.reduce((total,obj)=>parseFloat(obj.qty)+ total, 0);
    });
    orderProducts.forEach(function(orderProduct,index){
        let quantity = orderProduct.qty;
        let matchedShipmentItems = shipmentItems.filter(shipmentItem => orderProduct.product == shipmentItem.sdm_po_items_id.product && product.plant == shipmentItem.sdm_po_items_id.plant);
        //if(quantity>0){
            matchedShipmentItems.forEach(function(shipmentItem,index){
                if(quantity>0){
                    if(!shipmentItem.itemStatus){
                        let matchedPoItemIndex = orderItems.findIndex(orderItem => orderItem.sdm_po_items_id ==  shipmentItem.sdm_po_items_id.id);
                        if(matchedPoItemIndex>=0){
                            let orderQty = quantity>matchedShipmentItems[0].remaining_qty?matchedShipmentItems[0].remaining_qty:quantity
                            let previousQty = orderItems[matchedPoItemIndex].quantity;
                            orderItems[matchedPoItemIndex].quantity = previousQty + orderQty;

                            let shipmentItemIndex = shipmentItems.findIndex(sItem =>sItem.id == shipmentItem.id);
                            shipmentItems[shipmentItemIndex].itemStatus = quantity>shipmentItems[shipmentItemIndex].remaining_qty?true:false
                            testConsole.push({
                                id : shipmentItems[shipmentItemIndex].id,
                                status : shipmentItems[shipmentItemIndex].itemStatus
                            })
                            quantity = quantity - orderQty;
                            shipments.push({
                                sdm_shipments_id : shipmentItem.sdm_shipments_id.id
                            })
                            updateShipmentItems.push({
                                id : shipmentItem.id,
                                ordered_qty : shipmentItem.ordered_qty + orderQty,
                                remaining_qty : shipmentItem.remaining_qty - orderQty
                            })
                            let netValue = orderQty*shipmentItem.sdm_po_items_id.unit_price;
                            orderValue =  orderValue + netValue+(netValue*shipmentItem.sdm_po_items_id.tax_percentage)/100;
                        }else{
                            let orderQty = quantity>matchedShipmentItems[0].remaining_qty?matchedShipmentItems[0].remaining_qty:quantity
                            orderItems.push({
                                sdm_po_items_id : shipmentItem.sdm_po_items_id.id,
                                quantity : orderQty
                            })


                            let shipmentItemIndex = shipmentItems.findIndex(sItem =>sItem.id == shipmentItem.id);
                            shipmentItems[shipmentItemIndex].itemStatus = quantity>shipmentItems[shipmentItemIndex].remaining_qty?true:false
                            testConsole.push({
                                id : shipmentItems[shipmentItemIndex].id,
                                status : shipmentItems[shipmentItemIndex].itemStatus,
                                qty : quantity,
                                remaining : shipmentItems[shipmentItemIndex].remaining_qty
                            })
                            quantity = quantity - orderQty;
                            shipments.push({
                                sdm_shipments_id : shipmentItem.sdm_shipments_id.id
                            })
                            updateShipmentItems.push({
                                id : shipmentItem.id,
                                ordered_qty : shipmentItem.ordered_qty + orderQty,
                                remaining_qty : shipmentItem.remaining_qty - orderQty
                            })
                            let netValue = orderQty*shipmentItem.sdm_po_items_id.unit_price;
                            orderValue =  orderValue + netValue+(netValue*shipmentItem.sdm_po_items_id.tax_percentage)/100;
                        }
                    }
                }

            })  
        //}

    })
    orderObj[index] = {};
    orderObj[index].order_value = orderValue;
    orderObj[index].order_status = "open";
    orderObj[index].order_type = "use_and_pay";
    orderObj[index].order_date = product.order_date;
    orderObj[index].order_items = orderItems;
    orderObj[index].shipments = shipments;
    orderObj[index].po_number = product.po_number
})
items[0].json.uniqueProductPoPlantArr = uniqueProductPoPlantArr;
items[0].json.orderObj= orderObj;
items[0].json.updateShipmentItems= updateShipmentItems
items[0].json.testConsole = testConsole
return items;

let shipmentItems = $node["Create order obj"].json["updateShipmentItems"];
uniqueArr = Object.create(null),
    uniqueShipmentItemArr =  shipmentItems.filter(o => {
        var key = ['id'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });
let updateShipmentItemArr = [];

uniqueShipmentItemArr.forEach(function(sItem,index){
    let matchedShipmentItem = shipmentItems.filter(item =>item.id == sItem.id);
    let shipmentItem = matchedShipmentItem.pop();
    updateShipmentItemArr.push({
        id:shipmentItem.id,
        ordered_qty : shipmentItem.ordered_qty,
        remaining_qty : shipmentItem.remaining_qty
    })
})
items[0].json.uniqueShipmentItemArr = uniqueShipmentItemArr
items[0].json.updateShipmentItemArr = updateShipmentItemArr
return items;

let inventoryItems = $node["Get the Inventory items"].json["data"];
let issuances = $node["Function to get issuance data"].json["issuanceData"];
let updateIssuanceArr = [];
uniqueArr = Object.create(null),
    uniqueProductArr =  issuances.filter(o => {
        var key = ['product','plant'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });
uniqueProductArr.forEach(function(product,index){
    let matchedInventoryItems = inventoryItems.filter(inventoryItem =>inventoryItem.product == product.product && inventoryItem.plant == product.plant);
    if(matchedInventoryItems.length>0){
        let matchedIssuances = issuances.filter(issuance =>issuance.product == product.product && issuance.plant == product.plant);
        let qty = matchedIssuances.reduce((total,obj)=>obj.qty+ total, 0);
        let previousStock = matchedInventoryItems[0].current_stock;
        updateIssuanceArr.push({
            id : matchedInventoryItems[0].id,
            current_stock : previousStock - qty
        })
    }
})

items[0].json.uniqueProductArr = uniqueProductArr
items[0].json.updateIssuanceArr = updateIssuanceArr
return items;
