function convertAmountToWords(amount) {
  // Convert number to string
  var number = amount.toString();

  // Define arrays for one-digit and two-digit numbers
  var singleDigits = [
    "Zero", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"
  ];

  var twoDigits = [
    "", "Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen",
    "Sixteen", "Seventeen", "Eighteen", "Nineteen"
  ];

  var tensMultiple = [
    "", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"
  ];

  var tensPower = ["Hundred", "Thousand", "Million", "Billion"];

  // Function to convert two-digit or one-digit number to words
  function convertTwoDigitsOrOne(number) {
    var num = parseInt(number, 10);
    var words = "";

    if (num < 10) {
      words = singleDigits[num];
    } else if (num >= 10 && num < 20) {
      words = twoDigits[num - 10];
    } else {
      var tens = Math.floor(num / 10);
      var ones = num % 10;
      words = tensMultiple[tens] + " " + singleDigits[ones];
    }

    return words;
  }

  // Function to convert three-digit number to words
  function convertThreeDigits(number) {
    var num = parseInt(number, 10);
    var words = "";
    var hundreds = Math.floor(num / 100);
    var remainder = num % 100;

    if (hundreds > 0) {
      words = singleDigits[hundreds] + " " + tensPower[0];
    }

    if (remainder > 0) {
      words += " " + convertTwoDigitsOrOne(remainder);
    }

    return words;
  }

  // Split the number into groups of three digits
  var numGroups = [];
  while (number.length > 0) {
    numGroups.push(number.slice(-3));
    number = number.slice(0, -3);
  }

  // Convert each group to words
  var words = "";
  for (var i = 0; i < numGroups.length; i++) {
    var groupWords = convertThreeDigits(numGroups[i]);

    if (groupWords !== "") {
      words = groupWords + " " + tensPower[i] + " " + words;
    }
  }

  return words.trim()+' Rupees Only';
}
