let inventoryData = $node["Get the inventorty items other than sufficient qty"].json["data"];
let productIds = inventoryData.map(id=>id.product);
let negligable_plants = JSON.parse($node["Globals"].json["negligable_plants"]);
let categoryIds = inventoryData.map(id=>negligable_plants.includes(id.plant)?id.plant:id.product.slice(0,2));


items[0].json.categoryIds = categoryIds;
items[0].json.negligable_plants = negligable_plants;
items[0].json.productIds = productIds;
return items;

let prodBuyerData = $node["Get the buyers and ops"].json["data"];
let buyerEmails = [];
let opsEmails = [];
prodBuyerData.forEach(function(pbd,index){
  if(pbd.additional_buyers){
    pbd.additional_buyers.forEach(function(additionalBuyer,index){
      buyerEmails.push(additionalBuyer.email);
    })
  }
  buyerEmails.push(pbd.buyer.email);
  opsEmails.push(pbd.operation_agent.email)
})

items[0].json.buyerEmails = buyerEmails;
items[0].json.opsEmails = opsEmails;
return items;

let bpoProducts = $node["Check for products in bpo"].json["data"];
let inventoryData = $node["Get the inventorty items other than sufficient qty"].json["data"];
let prodVen = [];
let vendorPoObj = [];
let catBuyersOps = $node["Get the buyers and ops"].json["data"];
let buyers_data = $node["Check for buyers"].json["data"];
let operation_agent_data = $node["Check for ops"].json["data"];
let negligable_plants = JSON.parse($node["Globals"].json["negligable_plants"]);
let updateInventoryIds = [];
let poNumber = $node["Get the latest vmi record"].json["data"].length>0?$node["Get the latest vmi record"].json["data"][0]["id"]:$node["Globals"].json["po_prefix"]
inventoryData.forEach(function(product,index){
  let matchedBpoProduct = bpoProducts.filter(bpoProd=>bpoProd.product == product.product )
  if(matchedBpoProduct.length>0){
    updateInventoryIds.push(product.id);
    let prodVenObj = {
      id:product.id,
      product : product.product,
      vendorEmail : matchedBpoProduct[0].vendor,
      plant : product.plant,
      uom : product.uom,
      leadTime : product.lead_time,
      reOrderQuantity : product.re_order_quantity,
      tax:18,
      hsn:"1234",
      rate : product.avg_price,
      bpo : matchedBpoProduct[0].sd_bpo_vedanta_ref
    }
    prodVen.push(prodVenObj)
  }
})

let updateInventotyObj = {
  keys : updateInventoryIds,
  data: {
		status: "po_generated"
	}
}
uniqueArr = Object.create(null),
    uniqueVendorPlantArr = prodVen.filter(o => {
        var key = ['plant', 'vendorEmail'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });
uniqueVendorPlantArr.forEach(function(obj,index){
  poNumber = (parseInt(poNumber)+1).toString();
  let matchedProduct = prodVen.filter(pv=>obj.plant == pv.plant && obj.vendorEmail == pv.vendorEmail);
  let delivery_dates = [];
  let poItems = [];
  let net_po_value = matchedProduct.reduce((total,mp)=>parseFloat(mp.rate)*parseFloat(mp.reOrderQuantity)+ total, 0);
  let po_value = matchedProduct.reduce((total,mp)=>parseFloat(mp.rate)*parseFloat(mp.reOrderQuantity)+(parseFloat(mp.rate)*parseFloat(mp.reOrderQuantity)*mp.tax)/100+ total, 0);
  let buyers = [];
  let ops = [];
  let buyerEmails = [];
  let opsEmails = [];
  if(negligable_plants.includes(obj.plant)){
    let getCatBuy = catBuyersOps.filter(cbo=>cbo.id == obj.plant);
    if(getCatBuy[0].additional_buyers){
      getCatBuy[0].additional_buyers.forEach(function(ab,index){
        buyerEmails.push(ab.email);
      })
    }
    buyerEmails.push(getCatBuy[0].buyer.email)
    opsEmails.push(getCatBuy[0].operation_agent.email)
  }else {
    let catBuy = catBuyersOps.filter(cbo=>cbo.id == obj.product.slice(0,2));
    if(catBuy[0].additional_buyers){
      catBuy[0].additional_buyers.forEach(function(ab,index){
        catBuy.push(ab.email);
      })
    }
    buyerEmails.push(getCatBuy[0].buyer.email)
    opsEmails.push(getCatBuy[0].operation_agent.email)
  }
  let uniqueBuyerArr = buyerEmails.filter((value, index) => {
    return buyerEmails.indexOf(value) === index;
  });
  let uniqueOpsArr = opsEmails.filter((value, index) => {
    return opsEmails.indexOf(value) === index;
  });

  uniqueOpsArr.forEach(function(ops_email,index){
    let get_ops_id =  operation_agent_data.filter(oad=>oad.operation_agent.email == ops_email);
    ops[index]={};
    ops[index].sdm_operation_agents_id = get_ops_id[0].id;
  })
  uniqueBuyerArr.forEach(function(buyer_email,index){
    let get_buyer_id =  buyers_data.filter(bd=>bd.buyer.email == buyer_email);
    buyers[index]={};
    buyers[index].sdm_buyers_id = get_buyer_id[0].id;
  })
  matchedProduct.forEach(function(mp,index){
    let timeLead = parseFloat(mp.leadTime);
    let numDaysToAdd = timeLead - (timeLead*0.2);
    let today = new Date();
    let resultDate = new Date(today);
    resultDate.setDate(today.getDate() + parseFloat(numDaysToAdd));
    delivery_dates.push(resultDate);
    let net_price = mp.reOrderQuantity * mp.rate;
    poItems[index] = {};
    poItems[index].id = `${poNumber}-${index}`;
    poItems[index].product = mp.product;
    poItems[index].vendor =  mp.vendorEmail;
    poItems[index].net_price = net_price;
    poItems[index].gross_price = net_price+(net_price*mp.tax)/100 ;
    poItems[index].requested_qty = mp.reOrderQuantity;
    poItems[index].delivery_date = new Date(resultDate).toISOString();
    poItems[index].unit_price = mp.rate;
    poItems[index].tax_percentage = mp.tax;
    poItems[index].uom = mp.uom;
    poItems[index].plant = mp.plant;
    poItems[index].hsn = mp.hsn;
  })
  let maxDate = new Date(
  Math.max(
    ...delivery_dates.map(element => {
      return new Date(element);
      }),
    ),
  );
  vendorPoObj[index]={};
  vendorPoObj[index].vendor = obj.vendorEmail;
  vendorPoObj[index].id = `SD${obj.plant}${poNumber}`;
  vendorPoObj[index].po_date = new Date().toISOString();
  vendorPoObj[index].po_status = "draft";
  vendorPoObj[index].po_value = po_value;
  vendorPoObj[index].net_po_value = net_po_value;
  vendorPoObj[index].po_type = "use_and_pay";
  vendorPoObj[index].po_deliver_date = new Date(maxDate).toISOString();
  vendorPoObj[index].po_items = poItems;
  vendorPoObj[index].buyers = buyers;
  vendorPoObj[index].operation_agents = ops;
  vendorPoObj[index].bpo = obj.bpo;
  vendorPoObj[index].consignment_type = "vmi";
})

items[0].json.updateInventotyObj = updateInventotyObj;
items[0].json.prodVen = prodVen;
items[0].json.uniqueArr = uniqueVendorPlantArr;
items[0].json.poNumber = poNumber;
items[0].json.vendorPoObj = vendorPoObj;
return items;
