var someDate = new Date();

someDate.setDate(someDate.getDate() + 0);

var dd =parseInt(someDate.getDate());
var day = dd < 10 ? '0' + dd : dd;
var mm =parseInt(someDate.getMonth() + 1);
var month = mm < 10 ? '0' + mm : mm;
var y = someDate.getFullYear();

var someFormattedDate = y+'-'+month +'-'+day;

items[0].json.date=someFormattedDate

return items;

let paymentData = $node["Get the payment pending payments"].json["data"];
let tableData = '';
let borderStyle = "border: 1px solid black;border-collapse: collapse"
paymentData.forEach(function(payment,index){
  let sdInvoiceNumber = payment.invoice;
  let poNumber = payment.order_id? payment.order_id.po_number:"";
  let grnNumber = payment.grn_number?payment.grn_number:"";
  let paymentDue = payment.payment_aging?payment.payment_aging:"";
  let amountDue = payment.total_outstanding;
  let dpNumber = payment.dp_number;
  tableData=tableData+
      `<tr style='${borderStyle}'>`+
      `<td style='${borderStyle}'>${poNumber}</td>`+
      `<td style='${borderStyle}'>${sdInvoiceNumber}</td>`+
      `<td style='${borderStyle}'>${amountDue}</td>`+
      `<td style='${borderStyle}'>${paymentDue}</td>`+
      `<td style='${borderStyle}'>${grnNumber}</td>`+
      `<td style='${borderStyle}'>${dpNumber}</td>`+
      `</tr>`;
})
let tableHtml = `<table  style="color:#000000;width: auto;border: 1px solid black;border-collapse: collapse;">`+
    `<tr style='background-color:#dedfe0;border: 1px solid black;border-collapse: collapse'>`+
    `<th style='${borderStyle}'>Vedanta PO Number</th>`+
    `<th style='${borderStyle}'>Invoice Number</th>`+
    `<th style='${borderStyle}'>Amount Due</th>`+
    `<th style='${borderStyle}'>Overdue by</th>`+
    `<th style='${borderStyle}'>GRN No.</th>`+
    `<th style='${borderStyle}'>DP No.</th>`+
    `</tr>${tableData}`+
    `</table>`;

items[0].json.tableHtml = tableHtml;
items[0].json.overDueLink = `${$node["Get url and token"].json["0"]["mozart_url"]}/admin/content/sdm_payment_advise?bookmark=416`
return items;
