let responseData = [];
let order_data = $node["Get the order"].json["data"][0]
let files = [];
files.push(order_data.shipment.vendor_invoice,order_data.shipment.vendor_po_num.po_num.po_file)

if(files){
    files.forEach(function(upload_file,index){
        responseData.push({
            json: {
                "id":upload_file,
                "index":"data"+index,
                "dms_url":$node["Get url and token"].json["0"]["mozart_url"]
            }			
        });
    })
}


return responseData;

let binaries = {}, binary_keys = [],uploadDocument = [],extension = [];
let vedantaPoNumnber = $node["Get the order"].json["data"][0]["shipment"]["vendor_po_num"]["po_num"]["id"];
let vendorPoNumber =  $node["Get the order"].json["data"][0]["shipment"]["vendor_po_num"]["id"];
let sdInvoiceNumber = $node["Webhook"].json["body"]["invoice_number"];
let orderId = $node["Webhook"].json["body"]["order_id"];
let orderValue = $node["Webhook"].json["body"]["order_value"];
let vendorInvoiceNumber = $node["Get the order"].json["data"][0]["shipment"]["invoice_number"];
let borderStyle = "border: 1px solid black;border-collapse: collapse"

for([index, item] of items.entries()) {
    let extension = item.json.data.filename_disk.split(".")[1]
    item.binary.data.fileName = `${item.json.data.title}.${extension}`;
    binaries[`data_${index}.${extension}`] = item.binary.data;
    binary_keys.push(`data_${index}.${extension}`);
    uploadDocument.push(`input_file${index}:data_${index}.${extension}`);
}
let tableHtml = `<table  style="color:#000000;width: auto;border: 1px solid black;border-collapse: collapse;">`+
    `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>Vedanta PO</td>`+
    `<td style='${borderStyle}'>${vedantaPoNumnber}</td>`+
    `</tr>`+
     `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>Vendor PO</td>`+
    `<td style='${borderStyle}'>${vendorPoNumber}</td>`+
    `</tr>`+
    `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>SD Invoice Number</td>`+
    `<td style='${borderStyle}'>${sdInvoiceNumber}</td>`+
    `</tr>`+
    `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>Order ID</td>`+
    `<td style='${borderStyle}'>${orderId}</td>`+
    `</tr>`+
    `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>Order Value</td>`+
    `<td style='${borderStyle}'>${orderValue}</td>`+
    `</tr>`+
    `<tr style='${borderStyle}'>`+
    `<td style='${borderStyle}'>Vendor Invoice Number</td>`+
    `<td style='${borderStyle}'>${vendorInvoiceNumber}</td>`+
    `</tr>`+
    `</table>`;

return [{
    json: {
        binary_keys: binary_keys.join(','),
        uploadDocument : uploadDocument.join(','),
        tableHtml : tableHtml
    },
    binary: binaries
}];
