let poFiles=[];
let excelFiles=[];
items.forEach((item) => {
    const fullPath = item.json["path"].split("/");
    const fileName = fullPath[fullPath.length - 1]
    if(decodeURI(fileName).includes(".csv") || decodeURI(fileName).includes(".xlsx")){
        excelFiles.push({"file_name":decodeURI(fileName)});
    }
});

return [{
