let grnData = $node["Function to get grn data"].json["result"];
let currentIndex = $node["Start Loop"].json["index"];
let currentGrn = grnData[currentIndex];
let returnItems = [];
uniqueArr = Object.create(null),
    uniqueShipmentProductArr = currentGrn.filter(o => {
        var key = ['product', 'vendor_invoice','vendor_po','movement_type'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });
uniqueShipmentProductArr.forEach(function(obj,index){
    let matchedShipmentProduct =   currentGrn.filter(grn=>grn.product == obj.product && grn.vendor_po == obj.vendor_po && grn.movement_type == obj.movement_type && grn.vendor_invoice==obj.vendor_invoice);
    let totalQty = matchedShipmentProduct.reduce((total,obj)=>obj.qty+ total, 0)
    returnItems.push({
    json: {
       product : obj.product,
       vendor_invoice:obj.vendor_invoice,
       vendor_po:obj.vendor_po,
       plant : obj.plant,
       qty : totalQty,
       dms_url : $node["Get url and token"].json["0"]["mozart_url"],
       movement_type : obj.movement_type
    }
  });

})

return returnItems;

