let currentGrn = $node["Function to get product ids"].json["currentGrn"];
let productIds = $node["Function to get product ids"].json["productIds"];
let inventoryItems = $node["Get the inventory items"].json["data"];
let updateInventoryObj = [];
let returnItems = [];
inventoryItems.forEach(function(inventoryItem,index){
    let matchedProductPlant = currentGrn.filter(grn=>inventoryItem.product == grn.product && inventoryItem.plant == grn.plant && grn.movement_type == "accepted");
    if(matchedProductPlant.length>0){
        let totalQty = matchedProductPlant.reduce((total,obj)=>obj.qty+ total, 0) 
        updateInventoryObj.push({
            id:inventoryItem.id,
            current_stock:parseFloat(inventoryItem.current_stock) + totalQty,
            sd_consignment_stock : parseFloat(inventoryItem.current_stock) + totalQty,
            item_status : "po_not_generated"
        })
    }

})
updateInventoryObj.forEach(function(obj,index){  
    returnItems.push({
    json: {
       id:obj["id"],
       current_stock:obj["current_stock"],
       item_status:obj["item_status"],
       sd_consignment_stock:obj["sd_consignment_stock"],
       dms_url : $node["Get url and token"].json["0"]["mozart_url"]
    }
  });

})

return returnItems;

let invoiceData = [];
let returnItems = [];
items.forEach((item, index) => {
  //productIds.push(item.json["Material"])
  invoiceData.push({
    invoice_number_id  : item.json.data.sdm_shipments_id.id,
    shipment_items :item.json["data"]["sdm_shipments_id"]["shipment_items"] ,
    vendor_po : item.json["data"]["sdm_shipments_id"]["vendor_po_num"],
    invoice_number : item.json.data.sdm_shipments_id.invoice_number,
    sdm_po_items_id : item.json.data.sdm_po_items_id,
    accepted_qty : item.json.data.accepted_qty,
    rejected_qty : item.json.data.rejected_qty,
    received_qty : item.json.data.received_qty,
  })
})
uniqueArr = Object.create(null),
    uniqueShipmentArr = invoiceData.filter(o => {
        var key = ['invoice_number_id'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });


uniqueShipmentArr.forEach(function(obj,index){
    let shipmentStatus = '';
    let matchedShipmentItems = invoiceData.filter(invoice=>invoice.invoice_number_id == obj.invoice_number_id);
    let lastValue = matchedShipmentItems[matchedShipmentItems.length - 1];

    let shipmentItems = lastValue.shipment_items
    let shipmentItemLength = shipmentItems.length;
    let grnAcceptedItems = shipmentItems.filter(item=>item.grn_status == "grn_accepted");
    let grnRejectedItems = shipmentItems.filter(item=>item.grn_status == "grn_rejected");
    let grnPartiallyAcceptedItems = shipmentItems.filter(item=>item.grn_status == "grn_partially_accepted");
    if(shipmentItemLength == grnAcceptedItems.length ){
      shipmentStatus = "grn_completed"
    }else if(shipmentItemLength == grnRejectedItems.length ){
      shipmentStatus = "grn_cancelled"
    }else if(grnPartiallyAcceptedItems.length>0){
      shipmentStatus = "grn_partially_accepted"
    }else {
      shipmentStatus = "grn_pending"
    }
    returnItems.push({
    json: {
       id : obj.invoice_number_id,
       shipment_status:shipmentStatus,
       dms_url : $node["Get url and token"].json["0"]["mozart_url"]
    }
  });

})


items[0].json.invoiceData = invoiceData;
items[0].json.returnItems = returnItems;
return items;

