uniqueArr = Object.create(null),
    uniqueShipmentArr = invoiceData.filter(o => {
        var key = ['invoice_number_id'].map(k => o[k]).join('|');
        if (!uniqueArr[key]) {
            uniqueArr[key] = true;
            return true;
        }
    });


uniqueShipmentArr.forEach(function(obj,index){
    let shipmentStatus = '';
    let matchedShipmentItems = invoiceData.filter(invoice=>invoice.invoice_number_id == obj.invoice_number_id);
    let lastValue = matchedShipmentItems[matchedShipmentItems.length - 1];

    let shipmentItems = lastValue.shipment_items
    let shipmentItemLength = shipmentItems.length;
    let grnAcceptedItems = shipmentItems.filter(item=>item.grn_status == "grn_accepted" || item.resolution=="close_transaction" || item.resolution == null);
    if(shipmentItemLength == grnAcceptedItems.length ){
      let grnRejectedItems = shipmentItems.filter(item=>item.grn_status == "grn_rejected");
      let grnPartiallyAcceptedItems = shipmentItems.filter(item=>item.grn_status == "grn_partially_accepted");
      let grnCompletedItems = shipmentItems.filter(item=>item.grn_status == "grn_accepted");
      if(shipmentItemLength == grnCompletedItems.length){
        shipmentStatus = "grn_completed"
      }else if(shipmentItemLength == grnRejectedItems.length){
        shipmentStatus = "grn_cancelled"
      }else if(grnPartiallyAcceptedItems>0 || grnCompletedItems.length>0){
        shipmentStatus = "grn_partially_accepted"
      }

    }else {
      shipmentStatus = "grn_pending"
    }
    returnItems.push({
    json: {
       id : obj.invoice_number_id,
       shipment_status:shipmentStatus,
       dms_url : $node["Get url and token"].json["0"]["mozart_url"]
    }
  });

})


items[0].json.invoiceData = invoiceData;
items[0].json.returnItems = returnItems;
return items;

