let someDate = new Date();
let daysToAdd = [45,30,15,7];
let addedDays = [];

daysToAdd.forEach(function(numberOfDaysToAdd,index){
  someDate.setDate(someDate.getDate() + numberOfDaysToAdd);

  let dd =parseInt(someDate.getDate());
  let day = dd < 10 ? '0' + dd : dd;
  let mm =parseInt(someDate.getMonth() + 1);
  let month = mm < 10 ? '0' + mm : mm;
  let y = someDate.getFullYear();

  let someFormattedDate = y+'-'+month +'-'+day;
  addedDays.push(someFormattedDate);
})


items[0].json.addedDays=addedDays
return items;
