let followUpData =[];
let vendorFollowUpTable = [];
let emailData = []
items.forEach((item,index)=>{
    let ordered_qty = item.json.ordered_qty?item.json.ordered_qty:0
    followUpData[index]={};
    followUpData[index].vendor = item.json.email;
    followUpData[index].po_date = new Date(item.json.po_date).toLocaleDateString().split("/").join("-");
    followUpData[index].po_number = item.json.sdpo;
    followUpData[index].item_code = item.json.product;
    followUpData[index].material = item.json.product_name;
    followUpData[index].qty = item.json.shipment_status?(item.json.requested_qty - ordered_qty) + item.json.order_quantity:item.json.requested_qty
    followUpData[index].po_value = item.json.po_value;
    followUpData[index].delivery_date = new Date(item.json.delivery_date).toLocaleDateString();
    followUpData[index].delivery_status = item.json.deliverystatus;
    followUpData[index].due_days = item.json.due.days?item.json.due.days:0;
    followUpData[index].due_hours = item.json.due.hours;
    followUpData[index].delivery_address = item.json.delivery_address
})
let filteredFollowUpData = followUpData.filter(data=>data.qty >0);
const groupBy = key => array =>
  array.reduce((objectsByKeyValue, obj) => {
    const value = obj[key];
    objectsByKeyValue[value] = (objectsByKeyValue[value] || []).concat(obj);
    return objectsByKeyValue;
  }, {});
const groupByBrand = groupBy('vendor');
const result = groupByBrand(filteredFollowUpData);
let i = 0
for (const key in result) {
    let tableData= "";
    let tableHtml = "";
    let itemsData = result[key];
    itemsData.forEach(function(item,index){
      let style = "";
      let diffDays = ""
      if(item.due_days == 0){
        diffDays = `Overdue by ${Math.abs(item.due_hours)} hours`
        style = "background-color:#E35169"
      }else if(item.due_days < 0 ){
        let days = item.due_days == -1?2:item.due_days
        diffDays = `Overdue by ${Math.abs(days)} days`
        style = "background-color:#E35169"
      }else {
        let days = item.due_days == 1?2:item.due_days
        diffDays = `Due in ${days} days`
        style = "background-color:#FFFFFF"
      }
      tableData=tableData+
      `<tr style=${style}>`+
      `<td>${index+1}</td>`+
      `<td>${item["po_date"]}</td>`+
      `<td>${item["po_number"]}</td>`+
      `<td>${item["item_code"]}</td>`+
      `<td>${item["material"]}</td>`+
      `<td>${item["qty"] }</td>`+
      `<td>${item["po_value"] }</td>`+
      `<td>${item["delivery_date"] }</td>`+
      `<td>${diffDays}</td>`+
      `<td>${item["delivery_address"]}</td>`+
      `</tr>`;
    })
    tableHtml = tableHtml+ `<table  style="color:#fff;width: auto;">`+
    `<tr style="background-color:#a7d">`+
    `<th>SL.No</th>`+
    `<th>PO Date</th>`+
    `<th>SDPO No</th>`+
    `<th>ITEM CODE</th>`+
    `<th>Material List</th>`+
    `<th>Qty</th>`+
    `<th>SDPO Value</th>`+
    `<th>Item Delivery Date</th>`+
    `<th>Delivery Status</th>`+
    `<th>Plant Delivery Address</th>`+
    `</tr>${tableData}`+
    `</table>`;
    vendorFollowUpTable[i] = {};
    vendorFollowUpTable[i].vendor = key;
    vendorFollowUpTable[i].tableData = tableData;
    vendorFollowUpTable[i].tableHtml = tableHtml;

    emailData.push({
    json: {
       vendorFollowUpTable:vendorFollowUpTable[i]
      }
    });
    i++;
}
// return [{
//      json: {
//          followUpData: followUpData,
//          result : result,
//          vendorFollowUpTable:vendorFollowUpTable
//     }
// }];
  //  emailData.push({
  //   json: [{
  //      vendorFollowUpTable:vendorFollowUpTable
  //     }]
  //   });

return emailData;
