let followUpData =[];
let vendorFollowUpTable = [];
let emailData = []
let followUpDataIndex = 0;
let includeStatus = ["open","awaiting_irn","awaiting_asn"];
let borderStyle = 'border: 1px solid black;border-collapse: collapse';
items.forEach((item,index)=>{
    let checkRecordExists = followUpData.filter(data=>data.vendor == item.json.email && data.item_code == item.json.product);
    if(checkRecordExists.length == 0){
      let sameVendorProduct = items.filter(i=>i.json.product == item.json.product && i.json.email == item.json.email && includeStatus.includes(i.json.shipment_status));
      const qty = sameVendorProduct.reduce(function (result, product) {
        return result + (product.json.order_quantity?product.json.order_quantity:0);
      }, 0);
      let ordered_qty = item.json.ordered_qty?item.json.ordered_qty:0
      followUpData[followUpDataIndex]={};
      followUpData[followUpDataIndex].po_item =  item.json.id 
      followUpData[followUpDataIndex].vendor = item.json.email;
      followUpData[followUpDataIndex].vendor_name = item.json.vendor_name;
      followUpData[followUpDataIndex].po_date = item.json.po_date.replace("T00:00:00.000Z","").split("-").reverse().join("-");
      followUpData[followUpDataIndex].po_number = item.json.sdpo;
      followUpData[followUpDataIndex].item_code = item.json.product;
      followUpData[followUpDataIndex].material = item.json.product_name;
      followUpData[followUpDataIndex].qty = item.json.shipment_status?(item.json.requested_qty - ordered_qty) + qty:item.json.requested_qty
      followUpData[followUpDataIndex].po_value = item.json.po_value;
      followUpData[followUpDataIndex].item_rate = item.json.unit_price;
      followUpData[followUpDataIndex].delivery_date = item.json.delivery_date.replace("T00:00:00.000Z","").split("-").reverse().join("/");
      followUpData[followUpDataIndex].delivery_status = item.json.deliverystatus;
      followUpData[followUpDataIndex].due_days = item.json.due.days?item.json.due.days:0;
      followUpData[followUpDataIndex].due_hours = item.json.due.hours;
      followUpData[followUpDataIndex].delivery_address = item.json.delivery_address
      followUpDataIndex++
    }
})
let filteredFollowUpData = followUpData.filter(data=>data.qty >0);
const groupBy = key => array =>
  array.reduce((objectsByKeyValue, obj) => {
    const value = obj[key];
    objectsByKeyValue[value] = (objectsByKeyValue[value] || []).concat(obj);
    return objectsByKeyValue;
  }, {});
const groupByBrand = groupBy('vendor');
const result = groupByBrand(filteredFollowUpData);
let i = 0
for (const key in result) {
    let tableData= "";
    let tableHtml = "";
    let itemsData = result[key];
    itemsData.forEach(function(item,index){
      let style = "";
      let diffDays = ""
      if(item.due_days == 0){
        diffDays = `Due in ${Math.abs(item.due_hours)} hours`
        style = "background-color:#ffef96;border: 1px solid black;border-collapse: collapse"
      }else if(item.due_days < 0 ){
        let days = item.due_days == -1?2:item.due_days
        diffDays = `Overdue by ${Math.abs(days)} days`
        style = "background-color:#FF7276;border: 1px solid black;border-collapse: collapse"
      }else if(item.due_days<=10&&item.due_days>0 ){
        let days = item.due_days == 1?2:item.due_days
        diffDays = `Due in ${days} days`
        style = "background-color:#ffef96;border: 1px solid black;border-collapse: collapse"
      }
      else {
        diffDays = `Due in ${item.due_days} days`
        style = "border: 1px solid black;border-collapse: collapse"
      }
      tableData=tableData+
      `<tr style='${style}'>`+
      `<td style='${borderStyle}'>${index+1}</td>`+
      `<td style='${borderStyle}'>${item["po_date"]}</td>`+
      `<td style='${borderStyle}'>${item["po_number"]}</td>`+
      `<td style='${borderStyle}'>${item["item_code"]}</td>`+
      `<td style='${borderStyle}'>${item["material"]}</td>`+
      `<td style='${borderStyle}'>${item["qty"] }</td>`+
      `<td style='${borderStyle}'>${item["item_rate"] * item["qty"]}</td>`+
      `<td style='${borderStyle}'>${item["delivery_date"] }</td>`+
      `<td style='${borderStyle}'>${diffDays}</td>`+
      `<td style='${borderStyle}'>${item["delivery_address"]}</td>`+
      `</tr>`;
    })
    tableHtml = tableHtml+ `<table  style="color:#000000;width: auto;border: 1px solid black;border-collapse: collapse;">`+
    `<tr style='background-color:#9BC2E6;border: 1px solid black;border-collapse: collapse;scale: 0.9;'>`+
    `<th style='${borderStyle}'>SL.No</th>`+
    `<th style='${borderStyle}'>PO Date</th>`+
    `<th style='${borderStyle}'>SDPO No</th>`+
    `<th style='${borderStyle}'>ITEM CODE</th>`+
    `<th style='${borderStyle}'>Material List</th>`+
    `<th style='${borderStyle}'>Qty</th>`+
    `<th style='${borderStyle}'>Item Value</th>`+
    `<th style='${borderStyle}'>Item Delivery Date</th>`+
    `<th style='${borderStyle}'>Delivery Status</th>`+
    `<th style='${borderStyle}'>Plant Delivery Address</th>`+
    `</tr>${tableData}`+
    `</table>`;
    vendorFollowUpTable[i] = {};
    vendorFollowUpTable[i].vendor = key;
    vendorFollowUpTable[i].vendorName = itemsData[0].vendor_name;
    vendorFollowUpTable[i].tableData = tableData;
    vendorFollowUpTable[i].tableHtml = tableHtml;

    emailData.push({
    json: {
       vendorFollowUpTable:vendorFollowUpTable[i]
      }
    });
    i++;
}
