function render_page(pageData) {
  //check documents https://mozilla.github.io/pdf.js/
  let render_options = {
    //replaces all occurrences of whitespace with standard spaces (0x20). The default value is `false`.
    normalizeWhitespace: false,
    //do not attempt to combine same line TextItem's. The default value is `false`.
    disableCombineTextItems: false,
  };

  return pageData.getTextContent(render_options).then(function (textContent) {
    let lastY,
      text = "";
    for (let item of textContent.items) {
      text += `\n${item.str}`;
    }
    return text;
  });
}

let options = {
  pagerender: render_page,
  max: 200,
};

const binaryData = Buffer.from(items[0].binary.data.data, "base64");
let content = await pdf(binaryData, options);
const contentSubString =  content.text.substring(0,content.text.indexOf("APPENDIX I :"));
let contentSubStringArray = contentSubString.split("\n");
let paginationFound = false;
let productFound = false;
let productName = "";
let vendorName = "";
let vendorEmail = "";
let vendorFound = false;
let vendorNameFound = false;
let vendorEmailFound = false;
let productSection = false;
let x=[];

//Get the SUbString from DELIVERY SCHEDULE to Terms of Delivery to extract the product deliver date
let contentDeliveryScheduleSubString = content.text.substring(content.text.indexOf("DELIVERY SCHEDULE"),content.text.indexOf("Terms of Delivery :")).trim();
let dateSubString = contentDeliveryScheduleSubString.substring(contentDeliveryScheduleSubString.indexOf(" 1"));
let contentDeliveryScheduleSubStringArray = dateSubString.split("\n");

for(i=4;i<contentDeliveryScheduleSubStringArray.length;i+=5){
      product_deliverDate.push(contentDeliveryScheduleSubStringArray[i].trim());
}

//Loop through the ContentSubstring and extract the po details
for(i=0;i<contentSubStringArray.length;i++){
    if((contentSubStringArray[i].includes("Page") || paginationFound || contentSubStringArray[i].includes("Specification")) && productSection){
      if(contentSubStringArray[i].includes("Specification")){
        paginationFound = false;
      }else paginationFound = true;
    }
    else if(contentSubStringArray[i].includes("PO Number")){
      let splitPoString = contentSubStringArray[i+1].split(":");
      po_number = splitPoString[1].trim();
    }else if(contentSubStringArray[i].includes("PO Date")){
      let splitPoDateString = contentSubStringArray[i+1].split(":");
      po_date = splitPoDateString[1].trim().split(".").reverse().join("-");
    }else if(contentSubStringArray[i].includes("Total PO Value")){
      po_value = contentSubStringArray[i+1].split(",").join('').trim();
      productSection = false
    }else if(contentSubStringArray[i].includes("Item code") && !contentSubStringArray[i-3].includes("PURCHASE ORDER CONTINUATION")){
      product_code.push(contentSubStringArray[i+9]);
      plant.push(contentSubStringArray[i+10]);
      uom.push(contentSubStringArray[i+11]);
      product_quantity.push(contentSubStringArray[i+12].trim());
      rates.push(contentSubStringArray[i+13].split(",").join(''));
      productSubTotal.push(contentSubStringArray[i+14].split(",").join(''));
      productSection = true;
      productFound = true;
      i=i+14;  
    }else if(contentSubStringArray[i].includes("GSTN No") && !contentSubStringArray[i+4].includes("CONFIRMATION/ACKNOWLEDGEMENT") && !contentSubStringArray[i+2].includes("Total PO Value")){
      product_code.push(contentSubStringArray[i+3]);
      plant.push(contentSubStringArray[i+4]);
      uom.push(contentSubStringArray[i+5]);
      product_quantity.push(contentSubStringArray[i+6].trim());
      rates.push(contentSubStringArray[i+7].split(",").join(''));
      productSubTotal.push(contentSubStringArray[i+8].split(",").join(''));
      productFound = true;
      i=i+8;
    }else if(productFound){ 
      if(contentSubStringArray[i].includes("Vendor Details:") || contentSubStringArray[i].includes("HSN Code")){
        if(contentSubStringArray[i].includes("Vendor Details:")){
          vendorFound = true;
        }

        productFound = false;
        if(productName.includes("IN :Integrated GST")){
          let result = productName.substring(productName.indexOf("IN :Integrated GST"));
          taxPercentages.push(result.substring(0,result.indexOf(")"))+1);
          let getTaxValue = result.substring(result.indexOf(")")+1).trim().split(" ")[0];
          taxValues.push(getTaxValue);
          productName = productName.replace(result.substring(result.indexOf(")")+1,0),'');
          productName = productName.replace(getTaxValue,"")
        }
        product_name.push(productName);
        productName = "";
      }else{
        productName = productName + contentSubStringArray[i]
      }
    }else if(!vendorFound && contentSubStringArray[i].includes("Vendor Details:")){
      vendorFound = true;
    }
    else if(vendorFound){
      if(contentSubStringArray[i].includes("Email ID")){
        vendorNameFound = true;
        vendorFound = false;
        let vendorNameString = vendorName.split("-")
        if(vendorNameString[1].includes("IN :Integrated GST")){
          let result = vendorNameString[1].substring(vendorNameString[1].indexOf("IN :Integrated GST"));
          taxPercentages.push(result.substring(0,result.indexOf(")")));
          let getTaxValue = result.substring(result.indexOf(")")+1).trim().split(" ")[0];
          taxValues.push(getTaxValue.split(",").join('').trim());
        }
        let extractVendorName = vendorNameString[1].includes("IN :Integrated")?vendorNameString[1].substring(0,vendorNameString[1].indexOf("IN :Integrated")):vendorNameString[1];
        vendor_name.push(extractVendorName.trim());
        vendorName = "";

      }else{
        vendorName = vendorName + contentSubStringArray[i]
      }
    }else if(vendorNameFound){
      if(contentSubStringArray[i].includes("Contact Details")){
        vendorNameFound = false;
        vendorEmailFound = true;
        let vendorEmailString = vendorEmail?vendorEmail:contentSubStringArray[i-1].split("-");
        let vendorContactString = contentSubStringArray[i].split("-")
        vendor_phone.push(vendorContactString[1]);
        vendor_email.push(vendorEmail?vendorEmail.trim():vendorEmailString[1].trim());
        vendorEmail = "";
      }
      else{
        vendorEmail = vendorEmail + contentSubStringArray[i];
      }
    }
    else if(contentSubStringArray[i].includes("IN :Integrated")){
       x.push(contentSubStringArray[i+1]);
       let getTaxes = contentSubStringArray[i+1].split(",").join('').trim()
       taxValues.push(getTaxes??getTaxes);
       taxPercentages.push(contentSubStringArray[i]);
      //taxValues.push(contentSubStringArray[i+1].includes(',')?contentSubStringArray[i+1].split(",").join('').trim():contentSubStringArray[i+1].trim());
    }else if(contentSubStringArray[i].includes("HSN Code")){
      let hsnCodeString = contentSubStringArray[i].split(":");
      hsn_code.push(hsnCodeString[1].trim());
    }else if(contentSubStringArray[i].includes("Ariba Vendor ID")){
      let aribaVendorIdString = contentSubStringArray[i+1].split("-");
      aribaVendorId.push(aribaVendorIdString[1].trim());
    }
}

