						: '';


                let tableData = '';
let borderStyle = "border: 1px solid black;border-collapse: collapse;text-align: center; vertical-align: middle;"
mailItem.shipments.forEach(function(element,index){
  let link = `${mailItem.SHIPMENT_LINK}${element.id}`
  tableData=tableData+
      `<tr style='${borderStyle}'>`+
      `<td style='${borderStyle}'>${element.invoice_number}</td>`+
      `<td style='${borderStyle}'>${element.vendor_po_number.po_number}</td>`+
      `<td style='${borderStyle}'><a href=${link}>Link</a></td>`+
      `</tr>`;
})
let tableHtml = `<table  style="color:#000000;width: 100%;border: 1px solid black;border-collapse: collapse; height: 70px;">`+
