    `<th style='${borderStyle}'>PO Number</th>`+
    `<th style='${borderStyle}'>Link</th>`+
    `</tr>${tableData}`+
    `</table>`;

    mailItem.tableHtml=tableHtml;
    returnItems.push({
        json:mailItem
    });
})

return returnItems;

