let shipments =  $node["Webhook"].json["body"]["shipments"]
let shipmentLink = $node["Webhook"].json["body"]["SHIPMENT_LINK"];
let tableData = '';
let borderStyle = "border: 1px solid black;border-collapse: collapse;text-align: center; vertical-align: middle;"
shipments.forEach(function(element,index){
  let link = `${shipmentLink}${element.id}`
  tableData=tableData+
      `<tr style='${borderStyle}'>`+
      `<td style='${borderStyle}'>${element.invoice_number}</td>`+
      `<td style='${borderStyle}'>${element.vendor_po_number.po_number}</td>`+
      `<td style='${borderStyle}'><a href=${link}>Link</a></td>`+
      `</tr>`;
})
let tableHtml = `<table  style="color:#000000;width: 100%;border: 1px solid black;border-collapse: collapse; height: 70px;">`+
    `<tr style='background-color:#dedfe0;border: 1px solid black;border-collapse: collapse;'>`+
    `<th style='${borderStyle}'>Invoice Number</th>`+
    `<th style='${borderStyle}'>PO Number</th>`+
    `<th style='${borderStyle}'>Link</th>`+
    `</tr>${tableData}`+
    `</table>`;

items[0].json.tableHtml = tableHtml;
return items;
